tinymce.PluginManager.add('headers', function (editor) {

  editor.addButton('choosehead', {
    type: 'listbox',
    text: 'H',
    icon: false,
    onselect: function (e) {
      editor.execCommand('FormatBlock', false, this.value());
    },
    values: [{
      text: 'H1',
      value: 'h1'
    }, {
      text: 'H2',
      value: 'h2'
    }, {
      text: 'H3',
      value: 'h3'
    }, {
      text: 'H4',
      value: 'h4'
    }, {
      text: 'H5',
      value: 'h5'
    }, {
      text: 'H6',
      value: 'h6'
    }],
    onPostRender: function () {
      // this.value('h2');
    }
  });
});
